import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCookies } from 'react-cookie';

function BuyStock() {
    const navigate = useNavigate();
    const [cookies] = useCookies(['userRole']);
    const userRole = cookies.cookieName;
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [limitPrice, setLimitPrice] = useState('');
    const [expireDate, setExpireDate] = useState('');
    const location = useLocation();
    const stock_ticker = location.state.stock;

    useEffect(() => {
        const response = fetch(`/users//`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                stock_ticker: stock_ticker
            })
        });
        const data = response.json();
        setPrice(data);
    }, []);



    const handleBuy = async () => {
        const response = await fetch(`/users/buy-stock/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                stock_ticker: stock_ticker,
                username: localStorage.getItem("username"),
                quantity: parseInt(quantity)
            })
        });
        const data = await response.json();
        if (response.ok) {
            alert('Stock bought successfully!');
            navigate('/assets');
        } else {
            alert(data.message);
        }
    };

    const handleSetLimitOrder = async () => {
        const response = await fetch(`/users/limit-order-buy`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                stock_ticker: stock_ticker,
                username: localStorage.getItem("username"),
                quantity: parseInt(quantity),
                expire_date: expireDate     
            })
        });
        const data = await response.json();
        // handle success or error
        if (response.ok) {
            alert('Limit order set successfully!');
            navigate('/assets');
        } else {
            alert(data.message);
        }
    }; 

    if(userRole === "user") {
    return (
        <div>
            <h2>Buy {stock_ticker} {price.price} </h2>
            <div>
                <label>
                    Quantity:
          <input        type="number" 
                        value={quantity}
                        onChange={e => setQuantity(e.target.value)} 
                    />
                </label>
            </div>
            <div>
            </div>
            <button onClick={handleBuy}>Buy</button>
            <div>
            <label>
                    Quantity:
          <input
                        type="number"
                        value={limitQuantity}
                        onChange={(event) => setLimitQuantity(event.target.value)}
                    />
                </label>
                <label>
                    Price:
          <input
                        type="number"
                        value={limitPrice}
                        onChange={(event) => setLimitPrice(event.target.value)}
                    />
                </label>
                <label>
                    Expire Date:
          <input 
                        type="date"
                         value={expireDate} 
                         onChange={e => setExpireDate(e.target.value)} />
                </label>
            </div>
            <button onClick={handleSetLimitOrder}>Set Limit Order</button>
        </div>
    );
    } else {
        navigate("/login");
    }
}

export default BuyStock;
