import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Cookies from 'universal-cookie';

const Portfolio = () => {
    const [userStockData, setUserStockData] = useState([]);
    const [userCashData, setUserCashData] = useState();
    const [comparator, setComparator] = useState(false);
    const cookies = new Cookies();
    const userRole = cookies.get('UserRole');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch market data from backend API
        const fetchAssetsData = async () => {
            const response = await fetch("/users/view-assets", {
                method: 'GET',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const data = await response.json();
            if (response.ok) {
                //alert('Successful!');
                setUserStockData(data.stocks);
            } else {
                navigate("/login");
                //alert(data.message);
            }
        };
        fetchAssetsData();

    }, []);

    //console.log(comparator);

    if(userRole === "user") {
        console.log(userStockData.length);
    return (
        <div>
            <h2></h2>
                    { userStockData.length > 0  ? (
                    <div> 
                        <div>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Ticker</th>
                                            <th>Quantity</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                    {Object.entries(userStockData).map(([key, value]) => (
                                        <tr key={key}>
                                            <td>{key}</td>
                                            <td>{value}</td>
                                            <td><Link to={{ pathname: `/sell-stock/${key}`, state: { key } }}>Sell</Link></td>
                                        </tr>
                                        ))}
                                    </tbody>
                                </table>

                            </div>
                            <table>
                                <tr>
                                    <thead>
                                        <tr>
                                            <th>Cash</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td>{userCashData}</td>
                                        </tr>
                                    </tbody>
                                </tr>
                            </table>
                        </div>
                           ) : (
                            <div>
                                <p> You currently have no stocks! </p>
                                </div>
                           ) }
        </div>
    );
    } else {
        navigate("/login");
    }

}

export default Portfolio;
