import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
const LoginPage = () => {
  const navigate = useHistory();
  const [username, setUsername] = useState("");
  const [pwd, setPwd] = useState("");
  const [selectedRole, setSelectedRole] = useState('user');

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleSubmit = async (event) => {
    const accountUser = {
        username: username,
        pwd: pwd
    };
    event.preventDefault();
    // Handle form submission based on selected role
    if (selectedRole === 'admin') {
        const response = await fetch(`/auth/admin/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ accountUser })
        });
        const data = await response.json();
        if (response.ok) {
            alert('Login successful!');
            history.push('/assets');
        } else {
            alert(data.message);
        } 
    } else {
      // Handle user login
      const response = await fetch(`/auth/admin/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ accountUser })
        });
       const data = await response.json();
        if (response.ok) {
        alert('Log in successful!');
        history.push('/assets');
        } else {
            alert(data.message);
        } 
    }
  };

  return (
    <div>
      <h1>Login Page</h1>
      <div>
        <h2>Select Role</h2>
        <label>
          <input type="radio" value="admin" checked={selectedRole === 'admin'} onChange={handleRoleChange} />
          Admin
        </label>
        <label>
          <input type="radio" value="user" checked={selectedRole === 'user'} onChange={handleRoleChange} />
          User
        </label>
      </div>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="text" name="email" onChange={(event) => setUsername(event.target.value)}/>
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" onChange={(event) => setPwd(event.target.value)} />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      <p>
                Don't have an account? <Link to="/signup">Sign up</Link> now!
      </p>
    </div>
  );
};

export default LoginPage;
