import React, { useState, useEffect } from "react";
import MarketScheduleForm from "./MarketScheduleForm";
import { useCookies } from 'react-cookie';
import { useNavigate } from "rea"

function MarketSchedule() {
    const [cookies] = useCookies(['userRole']);
    const userRole = cookies.cookieName;
    const history = useHistory();

    const handleUpdateSchedule = async (updatedSchedule) => {
        const response = await fetch("/admin/change-schedule", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedSchedule),
        });
        const data = await response.json();
            if (response.ok) {
                alert('Successful!');
            } else {
                alert(data.message);
            };

    };

    if(userRole === "admin") {
    return (
        <div>
            <h2>Market Schedule</h2>
            <MarketScheduleForm onUpdateSchedule={handleUpdateSchedule} />
        </div>
    );
    } else {
        history.push("/login");
    }
}

export default MarketSchedule;
