import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { useCookies } from 'react-cookie';

const Portfolio = () => {
    const [userStockData, setUserStockData] = useState([]);
    //const [stockData, setStockData] = useState([]);
    const [cookies] = useCookies(['userRole']);
    const userRole = cookies.cookieName;
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch market data from backend API
        const fetchAssetsData = async () => {
            const response = await fetch("/users/view-assets", {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ username: localStorage.getItem("username") })
            })
            const data = await response.json();
            if (response.ok) {
                alert('Successful!');
                setUserStockData(data);
            } else {
                alert(data.message);
            }
        };
        fetchAssetsData();
    }, []);

    if(userRole === "user") {
    return (
        <div>
            <h2>My Portfolio</h2>
            {userStockData.map((portfolio) => (

            <div>
                {portfolio.stock.map((stock, index) => (
            <table>
                <thead>
                    <tr>
                        <th>Ticker</th>
                        <th>Quantity</th>
                    </tr>
                </thead>
                <tbody>
                        <tr key={index2}>
                            <td>{stock.stock_ticker}</td>
                            <td>{stock.quantity}</td>
                            <td><Link to={{ pathname: `/sell-stock/${stock.stock_ticker}`, state: { stock } }}>Sell</Link></td>
                        </tr>

                </tbody>
            </table>
                 ))}
            <table>
                <tr>
                <thead>
                    <tr>
                        <th>Cash</th>
                    </tr>
                </thead>
                <tbody>
                        <tr>
                            <td>{portfolio.cash}</td>
                        </tr>
                </tbody>
                </tr>
            </table>
            </div>

             ))}

        </div>
    );
    } else {
        navigate("/login");
    }

}

export default Portfolio;
